from concurrent.futures import ThreadPoolExecutor
import cdsapi
import os

# Create a CDS API client object
c = cdsapi.Client()

# Define a function to call the CDS API and retrieve data
def cds_api_call(year, month, variable, name):
    result = c.service(
        "tool.toolbox.orchestrator.run_workflow.user-apps.app-c3s-daily-era5-statistics",
        params={
            "code": code,
            "kwargs":{
                "area": {
                    "lat": [-90, 90],
                    "lon": [-180, 180]
                },
                "dataset": "reanalysis-era5-single-levels",
                "frequency": "1-hourly",
                "grid_e5": "1.0/1.0",
                "month": month,
                "pressure_level_e5sl": "-",
                "product_type": "reanalysis",
                "statistic": "daily_mean",
                "time_zone": "UTC+00:00",
                "variable_e5sl": variable,
                "year_e5sl": year
            },
            "workflow_name": "application"
        },
    )

    # Find the filename in the result
    temp_filename = None
    for item in result:
        if 'location' in item:
            temp_filename = item['location'].rsplit('/', 1)[-1]
            break
    print('\n')
    print(temp_filename)
    print('\n')
    if temp_filename:
        # Download the file with the temporary filename
        c.download(result)

        # After downloading, rename it
        os.rename(temp_filename, name)
    else:
        print("Could not find filename in API response")

